const Home = () => {

// define the callAPI function that takes a first name and last name as parameters
var callAPI = (firstName, lastName)=>{
  // instantiate a headers object
  var myHeaders = new Headers();

  // add content type header to object
  myHeaders.append("Content-Type", "application/json");

  // using built in JSON utility package turn object to s tring and store in a variable
  var raw = JSON.stringify({"firstName":firstName,"lastName":lastName});

  // create a JSON object with parameters for API call and store in a variable
  var requestOptions = {
    method: 'POST',
    headers: myHeaders,
    body: raw,
    redirect: 'follow'
    };

  console.log("Click")
     // make API call with parameters and use promises to get response
    fetch("https://56j70ao9r7.execute-api.us-east-1.amazonaws.com/dev")
    .then(response => response.text())
    .then(result => alert(JSON.parse(result).body))
    .catch(error => console.log('error', error));
  };


  return(
    <>
      <title>Home</title>
      <h1>Courses</h1>
      <form>
      <label>First Name :</label>
      <input type="text" id="fName"></input>
      <label>Last Name :</label>
      <input type="text" id="lName"></input>
      <button type="button" 
      onclick="callAPI(document.getElementById('fName').value,document.getElementById('lName').value)">
        Call API</button>
        </form>
    </>
  )
  };

  export default Home;
